import { useEffect } from "react";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateCard } from "../features/cards/cardSlice";

function Flashcard({ card }) {
  const [threeBox, setThreeBox] = useState(false);

  const [cardData, setCardData] = useState({
    front: card.front,
    back: card.back,
    date: card.date,
    delay: card.delay,
    reviews: card.reviews,
    tag: card.tag,
    id: card._id,
  });

  const dispatch = useDispatch();

  const onSubmit = (e) => {
    e.preventDefault();

    dispatch(updateCard({ ...cardData, date: 10, delay: 3 }));
  };

  const handleUpdate = (card) => {
    dispatch(updateCard(card));
  };

  const handleWrong = (cardData) => {
    const card = { ...cardData };
    card.date = new Date().setHours(0, 0, 0, 1).valueOf();
    card.delay = 0;
    card.reviews++;

    handleUpdate(card);
  };

  const handleCorrect = (cardData) => {
    const card = { ...cardData };


    let newDate = new Date().setDate(
      new Date().getDate() + (parseInt(card?.delay) + 1) * 3
    );

    card.date = new Date(newDate).setHours(0, 0, 0, 1);
    card.delay = parseInt((card.delay + 1)) * 3;
    card.reviews++;
    handleUpdate(card);

    handleUpdate(card);
  };

  const handleEasy = (cardData) => {
    const card = { ...cardData };

    let newDate = new Date().setDate(
      new Date().getDate() + (parseInt(card?.delay) + 1) * 3
    );

    card.date = new Date(newDate).setHours(0, 0, 0, 1);
    card.delay = parseInt((card.delay + 1)) * 3;
    card.reviews++;
    handleUpdate(card);
  };

  return (
    <>
      <div className="flashcardContainer">
        <div className="flashcardHeader">
          <div style={{ marginRight: "20px" }}>14 remaining</div>
        </div>

        <div className="flashcardBody">
          {card.front}
          {threeBox ? (
            <>
              <div
                style={{
                  height: "10px",
                  borderBottom: "1px solid black",
                  width: "220px",
                  marginBottom: "10px",
                }}
              />
              {card.back}
            </>
          ) : (
            <div
              style={{
                userSelect: "none",
                pointerEvents: "none",
                color: "rgba(1,1,1,0)",
                backgroundColor: "rgba(0,0,0,0)",
              }}
            >
              <div
                style={{
                  height: "11px",
                  width: "220px",
                  marginBottom: "10px",
                }}
              />
              .
            </div>
          )}
        </div>

        <div className="flashcardFooter">
          <div
            onClick={() => {
              setThreeBox(!threeBox);
            }}
            className={threeBox ? "threebox hide" : "threebox"}
          >
            {!threeBox && "Show Answer"}
          </div>
          <div
            onClick={() => {
              setThreeBox(!threeBox);
              handleWrong(cardData);
            }}
            className={threeBox ? "onebox l2" : "onebox l1"}
          >
            {threeBox && "Wrong"}
          </div>
          <div
            onClick={() => {
              setThreeBox(!threeBox);
              handleCorrect(cardData);
            }}
            className={threeBox ? "onebox c2" : "onebox c1"}
          >
            {threeBox && "Correct"}
          </div>
          <div
            onClick={() => {
              setThreeBox(!threeBox);
              handleEasy(cardData)
            }}
            className={threeBox ? "onebox r2" : "onebox r1"}
          >
            {threeBox && "Easy"}
          </div>
        </div>
      </div>
    </>
  );
}

export default Flashcard;
