import { useEffect } from "react";
import { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import Flashcard from "../components/Flashcard";
import { updateCard } from "../features/cards/cardSlice";

function Review({ cards }) {


    const cardsDue = cards.filter(card => new Date() > new Date(card.date))
    const [index, setIndex] = useState(0)

    const card = cardsDue[index]

    console.log(cardsDue)

  const [cardData, setCardData] = useState({
    front: card.front,
    back: card.back,
    date: card.date,
    delay: card.delay,
    reviews: card.reviews,
    tag: card.tag,
    id: card._id,
  });


  const dispatch = useDispatch();

  const onSubmit = (e) => {
    e.preventDefault();

    dispatch(updateCard({ ...cardData, date: 10, delay: 3 }));
  };

  return (
    <section>
        <center>
            {index}
        <Flashcard card={card} onIncrement={() => setIndex[index+1]} />
        </center>
      {cardData.front} - {cardData.back} - {cardData.delay}
      <br />
      {console.log(new Date(cardData.date))}
      {new Date() > new Date(cardData.date) && <>due</>}
      <br />
      <button onClick={onSubmit}>update</button>
    </section>
  );
}

export default Review;
